export default function MultipleChoice(props) {
	const termsArray = props.terms;
	let answerIndex = Math.floor(props.questionSeed * termsArray.length);

	//generate an array of 3 random terms that are not at answer index
	let randomTerms = [];
	for (let i = 0; i < 3; i++) {
		let randomIndex = Math.floor(Math.random() * termsArray.length);
		while (randomIndex === answerIndex) {
			randomIndex = Math.floor(Math.random() * termsArray.length);
		}
		randomTerms.push(termsArray[randomIndex]);
	}
	randomTerms.push(termsArray[answerIndex]);

	//shuffle randomTerms
	for (let i = randomTerms.length - 1; i > 0; i--) {
		let j = Math.floor(Math.random() * (i + 1));
		[randomTerms[i], randomTerms[j]] = [randomTerms[j], randomTerms[i]];
	}

	return (
		<a className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700 space-y-5">
			<ul className="grid gap-6 w-full md:grid-cols-2">
				{randomTerms.map((term) => {
					return <Choice potentialAnswer={term[0]} key={term[0]}></Choice>;
				})}
			</ul>
		</a>
	);
}

function Choice(props) {
	return (
		<li key={props.potentialAnswer + "li"}>
			<input key={props.potentialAnswer + "input"} type="radio" id="choice-big" name="choice" value="choice-big" className="hidden peer"></input>
			<label key={props.potentialAnswer + "label"} className="inline-flex justify-between items-center p-5 w-full text-gray-500 bg-white rounded-lg border border-gray-200 cursor-pointer dark:hover:text-gray-300 dark:border-gray-700 dark:peer-checked:text-blue-500 peer-checked:border-blue-600 peer-checked:text-blue-600 hover:text-gray-600 hover:bg-gray-100 dark:text-gray-400 dark:bg-gray-800 dark:hover:bg-gray-700">
				{props.potentialAnswer}
			</label>
		</li>
	);
}
