import "./App.css";
import Home from "./Home";
import Learn from "./Learn";
import {learnMode} from "./index.js";

function App() {
  //import variables from index.js
  const [learnMode, setLearnMode] = useState(learnMode);
  if(learnMode)
  {
  return (
    <div className="mx-auto my-auto w-3/4 h-3/4">
        <Home />
        <Learn />
    </div>
  );
  }
  else{
    return (
      <div className="mx-auto my-auto w-3/4 h-3/4">
          <Home />
      </div>
    );
  }
}

export default App;
