import "./App.css";
import {useState} from "react";
// import ReactDOMServer from "react-dom/server";

function App() {
	const challenges = [["NBA Points", "Mr. Schlenker", "/PDFs/NBA Points Challenge - CSML 1022.pdf"], ["Polling", "Andrew Young"]];
	const [currentChallenge, setChallenge] = useState(challenges[0]);

	const handleClick = index => {
		setChallenge(challenges[index]);
	  };

	return (
		<div className="grid grid-cols-2">
			<div className="relative mx-auto flex flex-col min-h-screen w-1/2 mt-12 space-y-10">
				{challenges.map((challenge, index) => (
					<a onClick={() => handleClick(index)} class="block p-6 bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
						<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{challenge[0]}</h5>
						<p class="font-normal text-gray-700 dark:text-gray-400">{challenge[1]}</p>
					</a>
				))}
			</div>
			<div className="relative mx-auto flex flex-col min-h-screen w-4/5 mt-12">
				<a href="#" class="float-right block p-6 bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700">
				<h2 class="text-4xl font-extrabold dark:text-white">{currentChallenge[0]}</h2>
					<p class="font-normal text-gray-700 dark:text-gray-400">{currentChallenge[1]}</p>
				</a>
			</div>
		</div>
	);
}

export default App;
