import "./App.css";
import {useState} from "react";
// import ReactDOMServer from "react-dom/server";

function App() {
	const challenges = [
		["NBA Points", "Mr. Schlenker", "NBA Points Challenge - CSML 1022.pdf"],
		["Polling", "Andrew Young", "Polling Data Challenge - CSML 1022.pdf"],
	];
	const [currentChallenge, setChallenge] = useState(challenges[0]);

	const handleClick = (index) => {
		setChallenge(challenges[index]);
	};

	return (
		<div className="grid grid-cols-3">
			<div className="col-span-1 relative mx-auto flex flex-col min-h-screen mt-12 w-5/6 space-y-10">
				{challenges.map((challenge, index) => (
					<a onClick={() => handleClick(index)} class="block p-6 bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
						<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{challenge[0]}</h5>
						<p class="font-normal text-gray-700 dark:text-gray-400">{challenge[1]}</p>
					</a>
				))}
			</div>
			<div className="col-span-2 relative mx-auto flex flex-col min-h-screen w-5/6 mt-12">
				<a href="#" class="float-right block p-6 bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700 space-y-5">
					<h2 class="text-4xl font-extrabold dark:text-white">{currentChallenge[0]}</h2>
					<p class="font-normal text-gray-700 dark:text-gray-400">{currentChallenge[1]}</p>
					<iframe src={process.env.PUBLIC_URL + currentChallenge[2]} width="100%" height="500px"></iframe>

					<label for="message" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-400">
						Compare your code to the optimal solution
					</label>
					<textarea id="message" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Type your code..."></textarea>
				</a>
			</div>
		</div>
	);
}

export default App;
