export default function Result(props) {
	const termsArray = props.terms;

	//count the number of 1s in the third element of every array in termsArray
	let learning = 0;
	let mastered = 0;
	let unknown = 0;
	for (let i = 0; i < termsArray.length; i++) {
		if (termsArray[i][2] === 0) {
			unknown++;
		}
		if (termsArray[i][2] === 1 || termsArray[i][2] === 2) {
			learning++;
		}
		if (termsArray[i][2] === 3) {
			mastered++;
		}
	}

	//sleep for 1 second to allow time for the user to see the result
	setTimeout(() => {
		document.addEventListener("keypress", eventAcceptor, false);
	}, 1000);

	function eventAcceptor(e) {
		var code = e.code;
		if (code === "Enter") {
			props.setLearnCounter();
			document.removeEventListener("keypress", eventAcceptor, false);
		}
	}

	return (
		<div className="space-x-10 w-full relative p-6 bg-white rounded-lg border border-gray-200 shadow-md  dark:bg-gray-800 dark:border-gray-700 h-52">
			<a href="#" class="flex p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
				<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Mastered</h5>
				<p class="font-normal text-gray-700 dark:text-gray-400">{mastered}</p>
			</a>
			<a href="#" class="flex p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
				<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Learning</h5>
				<p class="font-normal text-gray-700 dark:text-gray-400">{learning}</p>
			</a>
			<a href="#" class="flex p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
				<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Unknown</h5>
				<p class="font-normal text-gray-700 dark:text-gray-400">{unknown}</p>
			</a>
		</div>
	);
}
