export default function MultipleChoice(props) {
	const termsArray = props.terms;
	let answerIndex = Math.floor(props.questionSeed * termsArray.length);

  //generate an array of 3 random terms that are not at answer index
  let randomTerms = [];
  for (let i = 0; i < 3; i++) {
    let randomIndex = Math.floor(Math.random() * termsArray.length);
    while (randomIndex === answerIndex) {
      randomIndex = Math.floor(Math.random() * termsArray.length);
    }
    randomTerms.push(<Choice potentialAnswer={termsArray[randomIndex]}></Choice>);
  }
  randomTerms.push();


	return (
		<a className="block p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700 space-y-5">
      <Choice potentialAnswer={termsArray[Math.floor(Math.random() * termsArray.length)][0]}></Choice>
      <Choice potentialAnswer={termsArray[Math.floor(Math.random() * termsArray.length)][0]}></Choice>
      <Choice potentialAnswer={termsArray[Math.floor(Math.random() * termsArray.length)][0]}></Choice>
      <Choice potentialAnswer={termsArray[Math.floor(Math.random() * termsArray.length)][0]}></Choice>
		</a>
	);
}

function Choice(props) {
  return (
    <a className="block h-12 p-6 max-w-sm bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white">
				<p className="text-sm font-medium text-gray-900 dark:text-gray-300">{props.potentialAnswer}</p>
			</a>
    );
}
