import MultipleChoice from "./MultipleChoice";
import Written from "./Written";
import {useState} from "react";

export default function Learn(terms) {
	let progress = 50;
	const [wordsDone, setWords] = useState(0);

	//a function to choose either a multiple choice or written question with a random word from terms
	const chooseQuestion = () => {
		if (Math.floor(Math.random() * 2) === 0) {
			return <MultipleChoice word={terms[wordsDone]} />;
		} else {
			return <Written word={terms[wordsDone]} />;
		}
	};

	return (
		<div className="space-y-5">
			<div className="w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700">
				<div className="bg-blue-600 h-2.5 w-45 rounded-full" style={{width: progress + "%"}}></div>
			</div>
			{wordsDone % 10 !== 0 && chooseQuestion()}
			<p className="text-lg dark:text-white"> {JSON.stringify(terms)} </p>
		</div>
	);
}
